mapper.select:
SELECT sm.menu_id "id", sm.code "code", sm.icon "icon", ( CASE WHEN (sm2.menu_id = 0 OR sm2.menu_id IS NULL) THEN 0 ELSE sm2.menu_id END ) "parentId", sm.name "name", sm.url "url", sm.levels "levels", sm.menu_flag "ismenu", sm.sort "num" FROM (
SELECT b.employee_id "userId", b.employee_name "userName", b.avatar, a.role_id "roleId", a.permission_id "permissionId", a.permission_type "permissionType", a.content, a.relate_id "relateId", a.data_url "dataUrl" FROM ( SELECT ua.user_id, rp.role_id, rp.permission_id, p.permission_type, p.content, p.relate_id, p.data_url FROM sys_user_access ua, sys_role_permission rp LEFT JOIN sys_permission p ON rp.permission_id = p.permission_id WHERE ua.access_id = rp.role_id AND ua.user_id = #{userId} UNION ALL SELECT ua.user_id, NULL role_id, p.permission_id, p.permission_type, p.content, p.relate_id, p.data_url FROM sys_user_access ua, sys_permission p WHERE ua.access_id = p.permission_id AND ua.user_id = #{userId} ) a, oa_employee b WHERE b.employee_id = a.user_id AND b.employee_id = #{userId}
) pm, sys_menu sm LEFT JOIN sys_menu sm2 on sm.pcode = sm2.code WHERE sm.menu_id = pm.relateId AND sm.menu_flag = 'Y' ORDER BY sm.levels,sm.sort asc
